#pragma config(Motor,  port2,           Left,          tmotorServoContinuousRotation, openLoop, driveLeft)
#pragma config(Motor,  port9,           Right,         tmotorServoContinuousRotation, openLoop, reversed, driveRight)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#pragma platform(VEX)

//Competition Control and Duration Settings
#pragma competitionControl(Competition)
#pragma autonomousDuration(0)
#pragma userControlDuration(60)

#include "Vex_Competition_Includes.c"   //Main competition background code...do not modify!

//Global variables
float joystickAxis[7] = {0,0,0,0,0,0,0};
float smoothJoystickAxis[7] = {0,0,0,0,0,0,0};
long lastAccelTime = nSysTime;
float left_speed;
float right_speed;
int lowSpeed = 0;
bool tank = true;
bool arcade = false;
float power = 2;
float kAccelVal = 1;
int deadBand = 10;

