void Sense()
{
	joystickAxis[1] = vexRT[Ch1];
	joystickAxis[2] = vexRT[Ch2];
	joystickAxis[3] = vexRT[Ch3];
	joystickAxis[4] = vexRT[Ch4];
	joystickAxis[5] = vexRT[Ch5];
	joystickAxis[6] = vexRT[Ch6];

	if (vexRT[Btn8U] == 1){
		lowSpeed = 1;
	}
	else if (vexRT[Btn8D] == 0) {
		lowSpeed = 0;
	}

	if (vexRT[Btn8R] == 1) {
		tank = false;
		arcade = true;
	}
	else if (vexRT[Btn8L] == 1) {
		tank = true;
		arcade = false;
	}
}

void Accelerate()
{
	if(nSysTime-lastAccelTime>50 || lastAccelTime-nSysTime>50000) 
	{
		float tempVal;
		lastAccelTime=nSysTime;
		for(int i=1; i<=6; i++)
		{
			if(joystickAxis[i] > deadBand && joystickAxis[i] < -deadBand)
			{
				joystickAxis[i] = 0;
			}
			if(joystickAxis[i]>=0) {
				tempVal = pow(joystickAxis[i],power);
			}
			else {
				tempVal = -pow(-joystickAxis[i],power);
			}
			if (abs(tempVal - smoothJoystickAxis[i]) > kAccelVal) {
				//bring the motor value closer to the joystick by 1%
			smoothJoystickAxis[i] =+ (tempVal > smoothJoystickAxis[i]) ? kAccelVal : -1*kAccelVal;
			}
		}
	}
}

void DoWork()
{
	//66% NORMAL SPEED
	if (lowSpeed == 0) {
		left_speed = smoothJoystickAxis[3]*0.66;
		right_speed = smoothJoystickAxis[2]*0.66;
	}

	//33% BUMP SPEED
	else if (lowSpeed == 1) {
		left_speed = smoothJoystickAxis[3]*0.33;
		right_speed = smoothJoystickAxis[3]*0.33;
	}

	//Tank Drive
	if(tank) {
		motor[Left] = left_speed;
		motor[Right] = right_speed;
	}

	//Arcade drive
	if(arcade) {
		motor[Left] = (smoothJoystickAxis[2] + smoothJoystickAxis[3]);
		motor[Right] = (smoothJoystickAxis[2] - smoothJoystickAxis[3]);
	}
}

/////////////////////////////////////////////////////////////////////////////////////////
//
//                                 User Control Task
//
// This task is used to control your robot during the user control phase of a VEX Competition.
// You must modify the code to add your own robot specific commands here.
//
/////////////////////////////////////////////////////////////////////////////////////////

task usercontrol()
{
	// User control code here, inside the loop
	while (true)
	{
		// This is the main execution loop for the user control program. Each time through the loop
		// your program should update motor + servo values based on feedback from the joysticks.

		// .....................................................................................
		// Insert user code here. This is where you use the joystick values to update your motors, etc.
		// .....................................................................................
		Sense();
		Accelerate();
		DoWork();
	}
}
